import { useState } from "react";
import "./App.css";

function App() {
  const [ingredients, setIngredients] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);

  const getRecipe = async () => {
    setLoading(true);
    try {
      const res = await fetch("/api/recipe", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ingredients }),
      });
      const data = await res.json();

      if (Array.isArray(data.recipe)) {
        setRecipes(data.recipe);
      } else {
        // fallbackÏù¥ Î¨∏ÏûêÏó¥Ïùº Í≤ΩÏö∞
        setRecipes([
          {
            title: "Ï∂îÏ≤ú Ïã§Ìå®",
            ingredients: [],
            steps: [data.recipe],
          },
        ]);
      }
    } catch (err) {
      console.error("ÏöîÏ≤≠ Ïã§Ìå®:", err);
      setRecipes([
        {
          title: "ÏöîÏ≤≠ Ïã§Ìå®",
          ingredients: [],
          steps: ["ÏÑúÎ≤Ñ ÏöîÏ≤≠ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§."],
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Cook Buddy GPT üç≥</h1>
      <input
        type="text"
        placeholder="Ïòà: Í≥ÑÎûÄ, ÎåÄÌåå, Î∞•"
        value={ingredients}
        onChange={(e) => setIngredients(e.target.value)}
      />
      <button onClick={getRecipe} disabled={loading}>
        {loading ? (
          <>
            Î†àÏãúÌîº ÏÉùÏÑ± Ï§ë... <span className="spinner"></span>
          </>
        ) : (
          "Î†àÏãúÌîº Ï∂îÏ≤ú"
        )}
      </button>

      {recipes.map((r, i) => (
        <div key={i} className="recipe-card">
          <h2>{r.title}</h2>
          {r.ingredients.length > 0 && (
            <p><strong>Ïû¨Î£å:</strong> {r.ingredients.join(", ")}</p>
          )}
          <ol>
            {r.steps.map((s, j) => (
              <li key={j}>{s}</li>
            ))}
          </ol>
        </div>
      ))}
    </div>
  );
}

export default App;
