import React, { useState, useEffect } from "react";
import Login from "./components/Login";
import FridgePanel from "./components/FridgePanel";
import RecipePanel from "./components/RecipePanel";
import "./App.css";

function App() {
  const [userId, setUserId] = useState(localStorage.getItem("userId") || "");
  const [ingredientInput, setIngredientInput] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [mustHave, setMustHave] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [status, setStatus] = useState("");

  useEffect(() => {
    if (userId) {
      fetchFridge(userId);
    }
  }, [userId]);

  const handleLogin = (id) => {
    setUserId(id);
    localStorage.setItem("userId", id);
    fetchFridge(id);
  };

  const handleLogout = () => {
    localStorage.removeItem("userId");
    setUserId("");
    setIngredients([]);
    setMustHave([]);
    setRecipes([]);
    setError("");
  };

  const fetchFridge = async (uid) => {
    try {
      const res = await fetch(`/api/fridge/${uid}`);
      const data = await res.json();
      setIngredients(data.ingredients || []);
    } catch (err) {
      console.error("‚ùå ÎÉâÏû•Í≥† Ïû¨Î£å Ï°∞Ìöå Ïã§Ìå®", err);
    }
  };

  const handleAddIngredient = async () => {
    setStatus("");
    const newItems = ingredientInput.split(",").map((i) => i.trim()).filter(Boolean);
    if (!newItems.length) {
      setStatus("Ïû¨Î£åÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
      return;
    }

    try {
      const res = await fetch("/api/fridge/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, ingredients: newItems }),
      });
      const data = await res.json();
      if (data.success) {
        setIngredients(data.fridge);
        setStatus("‚úÖ Ïû¨Î£åÍ∞Ä Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.");
      } else {
        setStatus("‚ùå Ïã§Ìå®: " + data.message);
      }
    } catch (err) {
      console.error("‚ùå Ïû¨Î£å Ï∂îÍ∞Ä Ïã§Ìå®", err);
      setStatus("‚ùå ÏÑúÎ≤Ñ Ïò§Î•ò Î∞úÏÉù");
    }

    setIngredientInput("");
  };

  const handleRemoveIngredient = async (item) => {
    try {
      const res = await fetch("/api/fridge/remove", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, ingredient: item }),
      });
      const data = await res.json();
      if (data.success) {
        setIngredients(data.fridge);
        setMustHave((prev) => prev.filter((ing) => ing !== item));
        setStatus(`üóëÔ∏è '${item}' Ïû¨Î£åÍ∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.`);
      }
    } catch (err) {
      console.error("‚ùå Ïû¨Î£å ÏÇ≠Ï†ú Ïã§Ìå®", err);
    }
  };

  const toggleMustHave = (item) => {
    setMustHave((prev) =>
      prev.includes(item)
        ? prev.filter((i) => i !== item)
        : prev.length >= 3
        ? (alert("ÏµúÎåÄ 3Í∞úÍπåÏßÄÎßå ÏÑ†ÌÉùÌï† Ïàò ÏûàÏäµÎãàÎã§."), prev)
        : [...prev, item]
    );
  };

  const handleFridgeRecommend = async () => {
    if (!userId) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    setLoading(true);
    setError("");
    try {
      const res = await fetch("/api/recommend", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, mustHaveIngredients: mustHave }),
      });
      const data = await res.json();
      setRecipes(data.recipes);
    } catch (err) {
      console.error("‚ùå Î†àÏãúÌîº Ï∂îÏ≤ú Ïã§Ìå®", err);
      setError("Î†àÏãúÌîº Ï∂îÏ≤úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">üçΩÔ∏è Cook Buddy</h1>
      </header>

      {!userId ? (
        <main className="App-loginSection">
          <Login onLogin={handleLogin} />
        </main>
      ) : (
        <main className="App-main">
          <div className="App-userBar">
            <span><b>üë§ ÏÇ¨Ïö©Ïûê:</b> {userId}</span>
            <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          </div>

          <section className="App-panels">
            <FridgePanel
              ingredientInput={ingredientInput}
              setIngredientInput={setIngredientInput}
              ingredients={ingredients}
              mustHave={mustHave}
              toggleMustHave={toggleMustHave}
              handleAddIngredient={handleAddIngredient}
              handleRemoveIngredient={handleRemoveIngredient}
              status={status}
            />
            <RecipePanel
              handleFridgeRecommend={handleFridgeRecommend}
              loading={loading}
              error={error}
              recipes={recipes}
            />
          </section>
        </main>
      )}
    </div>
  );
}

export default App;
