import React, { useState, useEffect } from "react"; // ‚úÖ useEffect Ï∂îÍ∞ÄÎê®
import Login from "./components/Login";

function App() {
  const [userId, setUserId] = useState(localStorage.getItem("userId") || "");
  const [ingredientInput, setIngredientInput] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [mustHave, setMustHave] = useState([]); // ‚úÖ ÏÑ†ÌÉùÎêú ÌïÑÏàò Ïû¨Î£å
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [status, setStatus] = useState("");

  // ‚úÖ ÌéòÏù¥ÏßÄ Î°úÎìúÏãú userIdÍ∞Ä ÏûàÏúºÎ©¥ ÎÉâÏû•Í≥† Ïû¨Î£å Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (userId) {
      fetchFridge(userId);
    }
  }, [userId]);

  const handleLogin = (id) => {
    setUserId(id);
    localStorage.setItem("userId", id);
    fetchFridge(id);
  };

  const handleLogout = () => {
    localStorage.removeItem("userId");
    setUserId("");
    setIngredients([]);
    setMustHave([]); // ‚úÖ ÌïÑÏàò Ïû¨Î£å Ï¥àÍ∏∞Ìôî
    setRecipes([]);
    setError("");
  };

  const fetchFridge = async (uid) => {
    try {
      const res = await fetch(`/api/fridge/${uid}`);
      const data = await res.json();
      setIngredients(data.ingredients || []);
    } catch (err) {
      console.error("‚ùå ÎÉâÏû•Í≥† Ïû¨Î£å Ï°∞Ìöå Ïã§Ìå®", err);
    }
  };

  const handleAddIngredient = async () => {
    setStatus("");
    const newItems = ingredientInput.split(",").map((i) => i.trim()).filter(Boolean);
    if (!newItems.length) {
      setStatus("Ïû¨Î£åÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
      return;
    }

    try {
      const res = await fetch("/api/fridge/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, ingredients: newItems }),
      });
      const data = await res.json();
      if (data.success) {
        setIngredients(data.fridge);
        setStatus("‚úÖ Ïû¨Î£åÍ∞Ä Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.");
      } else {
        setStatus("‚ùå Ïã§Ìå®: " + data.message);
      }
    } catch (err) {
      console.error("‚ùå Ïû¨Î£å Ï∂îÍ∞Ä Ïã§Ìå®", err);
      setStatus("‚ùå ÏÑúÎ≤Ñ Ïò§Î•ò Î∞úÏÉù");
    }

    setIngredientInput("");
  };

  const handleRemoveIngredient = async (item) => {
    try {
      const res = await fetch("/api/fridge/remove", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, ingredient: item }),
      });
      const data = await res.json();
      if (data.success) {
        setIngredients(data.fridge);
        setMustHave((prev) => prev.filter((ing) => ing !== item)); // ‚úÖ ÏÇ≠Ï†ú Ïãú mustHaveÏóêÏÑúÎèÑ Ï†úÍ±∞
        setStatus(`üóëÔ∏è '${item}' Ïû¨Î£åÍ∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.`);
      }
    } catch (err) {
      console.error("‚ùå Ïû¨Î£å ÏÇ≠Ï†ú Ïã§Ìå®", err);
    }
  };

  // ‚úÖ ÌïÑÏàò Ïû¨Î£å ÏÑ†ÌÉù ÌÜ†Í∏Ä
  const toggleMustHave = (item) => {
    setMustHave((prev) => {
      if (prev.includes(item)) {
        return prev.filter((i) => i !== item);
      } else {
        if (prev.length >= 3) {
          alert("ÏµúÎåÄ 3Í∞úÍπåÏßÄÎßå ÏÑ†ÌÉùÌï† Ïàò ÏûàÏäµÎãàÎã§.");
          return prev;
        }
        return [...prev, item];
      }
    });
  };

  const handleFridgeRecommend = async () => {
    if (!userId) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    setLoading(true);
    setError("");
    try {
      const res = await fetch("/api/recommend", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, mustHaveIngredients: mustHave }), // ‚úÖ ÌïÑÏàò Ïû¨Î£å Ï†ÑÏÜ°
      });
      const data = await res.json();
      setRecipes(data.recipes);
    } catch (err) {
      console.error("‚ùå Î†àÏãúÌîº Ï∂îÏ≤ú Ïã§Ìå®", err);
      setError("Î†àÏãúÌîº Ï∂îÏ≤úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>üçΩÔ∏è Cook Buddy</h1>

      {!userId ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <div style={{ marginBottom: "20px" }}>
            <b>üë§ ÏÇ¨Ïö©Ïûê:</b> {userId}
            <button style={{ marginLeft: "10px" }} onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          </div>

          {/* ÎÉâÏû•Í≥† ÏôºÏ™Ω */}
          <div style={{ flex: 1, background: "#f4faff", padding: "20px", borderRadius: "8px" }}>
            <h2>üßä ÎÇ¥ ÎÉâÏû•Í≥†</h2>

            {/* ‚úÖ ÏûÖÎ†• ÌïÑÎìú: ÏÉÅÎã®ÏúºÎ°ú Ïù¥Îèô */}
            <div style={{ marginBottom: "16px" }}>
              <input
                value={ingredientInput}
                onChange={(e) => setIngredientInput(e.target.value)}
                placeholder="Ïòà: Í≥ÑÎûÄ, ÏñëÌåå"
                style={{ width: "70%", padding: "8px", marginRight: "10px" }}
              />
              <button onClick={handleAddIngredient}>Ï∂îÍ∞Ä</button>
              <p style={{ color: "green", marginTop: "8px" }}>{status}</p>
            </div>

            {/* ‚úÖ ÎÉâÏû•Í≥† Ïù¥ÎØ∏ÏßÄ + Ïû¨Î£å Î∞ïÏä§Îì§ */}
            <div
              style={{
                backgroundImage: `url('/fridge.png')`, // public/fridge.png ÎÑ£Í∏∞
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                width: "100%",
                height: "300px",
                display: "grid",
                gridTemplateColumns: "repeat(3, 1fr)",
                gap: "10px",
                padding: "20px",
                border: "1px solid #ccc",
                borderRadius: "8px",
                boxSizing: "border-box",
              }}
            >
              {ingredients.map((ing, i) => {
                const isSelected = mustHave.includes(ing);
                return (
                  <div
                    key={i}
                    onClick={() => toggleMustHave(ing)}
                    style={{
                      background: isSelected ? "#d0eaff" : "#fff",
                      border: "1px solid #aaa",
                      borderRadius: "6px",
                      padding: "8px",
                      textAlign: "center",
                      cursor: "pointer",
                      fontWeight: isSelected ? "bold" : "normal",
                      color: isSelected ? "#2a75f3" : "#000",
                      position: "relative",
                    }}
                  >
                    {isSelected && <span style={{ position: "absolute", top: 4, left: 6 }}>‚≠ê</span>}
                    <span>{ing}</span>
                    <button
                      onClick={(e) => {
                        e.stopPropagation(); // ÌÅ¥Î¶≠ Ï∂©Îèå Î∞©ÏßÄ
                        handleRemoveIngredient(ing);
                      }}
                      style={{
                        position: "absolute",
                        top: 4,
                        right: 6,
                        background: "none",
                        border: "none",
                        color: "red",
                        cursor: "pointer",
                      }}
                    >
                      ‚ùå
                    </button>
                  </div>
                );
              })}
            </div>
          </div>

        </>
      )}
    </div>
  );
}

export default App;
