import React from "react";
import "./FridgePanel.css";

function FridgePanel({
  ingredientInput,
  setIngredientInput,
  ingredients,
  mustHave,
  toggleMustHave,
  handleAddIngredient,
  handleRemoveIngredient,
  status,
}) {
  return (
    <div className="fridge-container">
      <h2>üßä ÎÇ¥ ÎÉâÏû•Í≥†</h2>

      <div className="fridge-input">
        <input
          value={ingredientInput}
          onChange={(e) => setIngredientInput(e.target.value)}
          placeholder="Ïòà: Í≥ÑÎûÄ, ÏñëÌåå"
        />
        <button onClick={handleAddIngredient}>Ï∂îÍ∞Ä</button>
        <p>{status}</p>
      </div>

      <div className="fridge-grid">
        {ingredients.map((ing, i) => {
          const isSelected = mustHave.includes(ing);
          return (
            <div
              key={i}
              className={`fridge-box ${isSelected ? "selected" : ""}`}
              onClick={() => toggleMustHave(ing)}
            >
              {isSelected && <span className="star">‚≠ê</span>}
              <span>{ing}</span>
              <button
                className="delete-button"
                onClick={(e) => {
                  e.stopPropagation();
                  handleRemoveIngredient(ing);
                }}
              >
                ‚ùå
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default FridgePanel;
