import React from "react";

function FridgePanel({
  ingredientInput,
  setIngredientInput,
  ingredients,
  mustHave,
  toggleMustHave,
  handleAddIngredient,
  handleRemoveIngredient,
  status,
}) {
  return (
    <div style={{ flex: 1, background: "#f4faff", padding: "20px", borderRadius: "8px" }}>
      <h2>üßä ÎÇ¥ ÎÉâÏû•Í≥†</h2>

      <div style={{ marginBottom: "16px" }}>
        <input
          value={ingredientInput}
          onChange={(e) => setIngredientInput(e.target.value)}
          placeholder="Ïòà: Í≥ÑÎûÄ, ÏñëÌåå"
          style={{ width: "70%", padding: "8px", marginRight: "10px" }}
        />
        <button onClick={handleAddIngredient}>Ï∂îÍ∞Ä</button>
        <p style={{ color: "green", marginTop: "8px" }}>{status}</p>
      </div>

      <div
        style={{
          backgroundImage: `url('/fridge.png')`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          width: "100%",
          height: "300px",
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: "10px",
          padding: "20px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          boxSizing: "border-box",
        }}
      >
        {ingredients.map((ing, i) => {
          const isSelected = mustHave.includes(ing);
          return (
            <div
              key={i}
              onClick={() => toggleMustHave(ing)}
              style={{
                background: isSelected ? "#d0eaff" : "#fff",
                border: "1px solid #aaa",
                borderRadius: "6px",
                padding: "8px",
                textAlign: "center",
                cursor: "pointer",
                fontWeight: isSelected ? "bold" : "normal",
                color: isSelected ? "#2a75f3" : "#000",
                position: "relative",
              }}
            >
              {isSelected && <span style={{ position: "absolute", top: 4, left: 6 }}>‚≠ê</span>}
              <span>{ing}</span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleRemoveIngredient(ing);
                }}
                style={{
                  position: "absolute",
                  top: 4,
                  right: 6,
                  background: "none",
                  border: "none",
                  color: "red",
                  cursor: "pointer",
                }}
              >
                ‚ùå
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default FridgePanel;
