// App.js
import React, { useState } from "react";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const userId = localStorage.getItem("userId");

  const handleFridgeRecommend = async () => {
    if (!userId) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    setLoading(true);
    setError("");
    try {
      const res = await fetch("/api/fridge-recommend", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId }),
      });
      const data = await res.json();
      setRecipes(data.recipes);
    } catch (err) {
      console.error("‚ùå Î†àÏãúÌîº Ï∂îÏ≤ú Ïã§Ìå®", err);
      setError("Î†àÏãúÌîº Ï∂îÏ≤úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App" style={{ padding: "20px" }}>
      <h1>üçΩÔ∏è Cook Buddy - ÎÉâÏû•Í≥† Î†àÏãúÌîº Ï∂îÏ≤ú</h1>

      <button onClick={handleFridgeRecommend}>
        üßä ÎÉâÏû•Í≥† Ïû¨Î£åÎ°ú Ï∂îÏ≤úÎ∞õÍ∏∞
      </button>

      {loading && <p>‚è≥ Ï∂îÏ≤ú Ï§ëÏûÖÎãàÎã§...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {recipes.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h2>Ï∂îÏ≤úÎêú Î†àÏãúÌîº</h2>
          {recipes.map((r, idx) => (
            <div key={idx} style={{ border: "1px solid gray", padding: "10px", marginBottom: "10px" }}>
              <h3>{r.title}</h3>
              <p><b>Main:</b> {r.mainIngredients.join(", ")}</p>
              <p><b>Extra:</b> {r.extraIngredients.join(", ")}</p>
              <ol>
                {r.steps.map((s, i) => <li key={i}>{s}</li>)}
              </ol>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
